def separa_info_genetica(informacion):
    return [informacion[0:6], informacion[6:11], informacion[11:14], informacion[14:16], informacion[16:]]

def arrbin_to_arrint(arr):
    aux = []
    for a in arr:
        aux.append(int(a,2))
    return aux

cromosoma = ''

calendario_split = [cromosoma[i:i+21] for i in range(0, len(cromosoma), 21)]

horario = []

for calendario in calendario_split:
    info = arrbin_to_arrint(separa_info_genetica(calendario))
    curso = {
        info[0]: {
            'ID_Seccion': info[1],
            'Dia': info[2],
            'Bloque': info[3],
            'Sala': info[4]
        }
    }
    horario.append(curso)

print(horario)